#encoding:utf-8from flask import Flaskfrom flask_login import LoginManagerfrom flask03.models.shop import dblogin_manager=LoginManager()def create_app():    app=Flask(__name__)    registe_blueprint(app)    app.config.from_object("flask03.setting")    app.config.from_object('flask03.secure')    db.init_app(app)  # 让它与flask 关联起来    db.create_all(app=app)    # 初始化对象    login_manager.init_app(app)    login_manager.login_view = 'web.lo'  # 告诉flask_login 没有授权时返回到登录页面    login_manager.login_message = u'请先登录或注册'    return appdef registe_blueprint(app):    from flask03.web import web    app.register_blueprint(web)